function website(software){this.software=software;const tag=this.software.tag
const id=this.software.id
const classN=this.software.classN;const name=this.software.name;const attr=this.software.attr;const def_text=this.software.def_text;const parent=this.software.parent;const style=this.software.styles;const script=this.software.scripts;if(typeof(tag)!=="string"){console.error("tag must be a string");return false;}
if(typeof(id)!=="string"){console.error("id must be a string");return false;}
if(typeof(classN)!=="string"){console.error("classN must be a string");return false;}
if(typeof(name)!=="string"){console.error("name must be a string");return false;}
if(!Array.isArray(attr)){console.error("attr must be a array");return false;}
if(!Array.isArray(style)){console.error("style must be a array");return false;}
if(!Array.isArray(script)){console.error("script must be a array");return false;}
for(a=0;a<attr.length;a++){for(b=0;b<style.length;b++){for(c=0;c<script.length;c++){let el=document.createElement(tag);el.id=id;el.className=classN;el.name=name;el.setAttribute(attr[a].type,attr[a].val);el.innerHTML=def_text;if(document.querySelector("."+el.className)||document.querySelector("#"+el.id)){document.querySelector("."+el.className).setAttribute(attr[a].type,attr[a].val);return false;}
document.querySelector(parent).appendChild(el);let link=document.createElement("link");link.href=style[b];link.rel="stylesheet";link.type="text/css";document.querySelector("head").appendChild(link);let source=document.createElement("script");source.src=script[c];source.type="text/javascript";document.querySelector("head").appendChild(source);}}}}